mapper.select:
select e.id,u.userNumber,u.userName as name,u.entryDate,IF(u.sex=1,'男','女') as sexName,c.`name` as companyName,c1.`name` as departmentName, p.`name` as postName,e.manager,e1.name as managerName,IF(u.state=0,'离职','在职') as stateName,e.skillPay,e.positionSalary,e.seniorityWage,e.foodSupplement, e.endowmentInsurance,e.unemploymentBenefits,e.medicalInsurance,e.accumulationFund,e.unionFees,e.sign from employee e LEFT JOIN users u on u.employeeId=e.id LEFT JOIN employee e1 on e1.id=e.manager LEFT JOIN company c on c.id=u.companyId LEFT JOIN company c1 on c1.id=u.departmentId LEFT JOIN post p on p.id=u.postId where u.state=1
and FIND_IN_SET(e.id,#{empId})
limit ${page},${pageSize}

select e.id,u.userNumber,u.userName as name,u.entryDate,IF(u.sex=1,'男','女') as sexName,c.`name` as companyName,c1.`name` as departmentName,p.`name` as postName,e.manager,e1.name as managerName,IF(u.state=0,'离职','在职') as stateName, e.skillPay,e.positionSalary,e.seniorityWage,e.foodSupplement,e.endowmentInsurance,e.unemploymentBenefits,e.medicalInsurance,e.accumulationFund,e.unionFees,e.sign from employee e LEFT JOIN users u on u.employeeId=e.id LEFT JOIN employee e1 on e1.id=e.manager LEFT JOIN company c on c.id=u.companyId LEFT JOIN company c1 on c1.id=u.departmentId LEFT JOIN post p on p.id=u.postId where ( e.address like concat('%',#{search},'%') or e.name like concat('%',#{search},'%') OR e.userNumber like concat('%',#{search},'%') )
and FIND_IN_SET(e.id,#{empId})
limit ${page},${pageSize}
